fieldvalidation.formula:
if($this.Header.GetFieldIndex("User_Header") != -1) {
    def userHeader = $this.Header.GetSection("User_Header");
    if(userHeader.$size > 0 && userHeader[0].IsNotNull("Validation_Flag")) {
        return false;
    }
}
return true;

def lines = Split("\r\n", $value);
if(lines.Length() > 1) {
    for(def i = 1; i < lines.Length(); ++i) {
        if(lines[i].FindFirst("/VALD/") != -1)
            return false;

    }
}
if(lines[0].FindFirst("/VALD/") != -1) {
    return VerifyFormat(lines[0], "/VALD/8!nCrLf") && 
             IsDate(Mid(lines[0], 6, 8), "yyyyMMdd");
}
        return true;

def lines = Split("\r\n", $value);
if(lines.Length() > 1 && lines[1].FindFirst("/SETC/") != -1) {
    if(!VerifyFormat(lines[1], "/SETC/3!aCrLf") || RightStr(lines[1], "/SETC/") == "" || 
         !IsISOCurrencyCode(RightStr(lines[1], "/SETC/")) || !lines[0].StartsWith("/VALD/"))
        return false;
    }
for(def i = 0; i < lines.Length(); ++i) {
    if(i != 1 && lines[i].FindFirst("/SETC/") != -1)
        return false;
}
if(lines.Length() > 2 && lines[2].StartsWith("/SRCE/") && !lines[1].StartsWith("/SETC/")) {
        return false;
}
for(def j = 0; j < lines.Length(); ++j) {
    if(j != 2 && lines[j].FindFirst("/SRCE/") != -1)
        return false;
    if(j == 2) {
        //Thirdline can have /SRCE/ only in the first 6 characters and nowhere
        def str = RightStr(lines[j], "/SRCE/");
        def index = FindFirst(RightStr(lines[j], "/SRCE/"), "/SRCE/");
        if(index != -1 || str.StartsWith("SRCE/"))
    return false;
    }
}
return true;
