fieldvalidation.formula:
if($this.Header.GetFieldIndex("User_Header") != -1) {
    def userHeader = $this.Header.GetSection("User_Header");
    if(userHeader.$size > 0 && userHeader[0].IsNotNull("Validation_Flag")) {
        return false;
    }
}
return true;

def header = $this.Header;
def appheader;
def commonRef = $value.Bank_Code_1 + $value.Location_Code_1 + $value.Reference_Code + 
                  $value.Bank_Code_2 + $value.Location_Code_2;
if(Length(commonRef) != 16){
  return false;
}
if(header.GetFieldCount() == 0 || 
     (header.GetFieldCount() > 0 && header.GetFieldIndex("Basic_Header") == -1)) {
	// No Header or FICC Header
	return true;
}
else if(header.GetFieldCount() == 4) {
	// Input-Output Header
    appheader = If(header.GetSection(1).$size > 0, 
                   header.GetSection(1)[0], 
                   header.GetSection(2)[0]);
}
else  {
    appheader = header.GetSection(1)[0];
}
def basicheader = header.GetSection(0)[0];
def identifier = appheader.GetString("Input/Output Identifier");
def recvAddr;
def senderAddr;
if(identifier == "I") {
	senderAddr = basicheader.GetString("LT Identifier");
    recvAddr = appheader.GetString("Receiver's Address");
}
else if(identifier == "O") {
    recvAddr = basicheader.GetString("LT Identifier");
   	senderAddr = appheader.GetString("Message Input Reference").Mid(6, 12);
}
// Remove Country Code from BIC
def senderBankLocCode = senderAddr.Mid(0, 8).Delete(4, 2);
def receiverBankLocCode = recvAddr.Mid(0, 8).Delete(4, 2);
def firstcode = Left(commonRef, 6);
def secondcode = Right(commonRef, 6);
def bankLocCodes = [senderBankLocCode, receiverBankLocCode];
def Field22Codes = [firstcode, secondcode];
return ContainsAll(Field22Codes, bankLocCodes);
