fieldvalidation.formula:
if($this.Header.GetFieldIndex("User_Header") != -1) {
    def userHeader = $this.Header.GetSection("User_Header");
    if(userHeader.$size > 0 && userHeader[0].IsNotNull("Validation_Flag")) {
        return false;
    }
}
return true;

if(B.F37U.$size > 0) {
    return SecCount(B.B1, "B1a.32M") == 0 && 
             B.B1.F17F.$size > 0 && B.B1.F14D.$size > 0;
} else {
   return SecCount(B.B1, "B1a.32M") == B.B1.B1a.$size && 
            B.B1.F17F.$size == 0 && B.B1.F14D.$size == 0;
}

if(E.F37U.$size > 0) {
    return SecCount(E.E1, "E1a.32M") == 0 && 
             E.E1.F17F.$size > 0 && E.E1.F14D.$size > 0;
} else {
   return SecCount(E.E1, "E1a.32M") == E.E1.E1a.$size && 
            E.E1.F17F.$size == 0 && E.E1.F14D.$size == 0;
}

if(C.$size > 0 && B.$size == 0 && E.$size == 0 && 
     F.$size == 0) {
    $type = A[0].F77H[0].Type;
    $floatingRateOption = C[0].F14F[0].Floating_Rate_Option;
    if(($type == "ISDA" && In($floatingRateOption, "FRF-TAM-CDC","FRF-T4M-CDC","FRF-T4M-CDCCOMP","FRF-TAG-CDC","FRF-TAG-CDCCOMP","FRF-TMP-CDCAVERAG")) || 
         ($type == "AFB" && In($floatingRateOption, "FRF-SWAP-AMR","FRF-SWAP-TMP-IF","FRF-SWAP-TMP-M","FRF-SWAP-T4M-AMR","FRF-CAP-TAM","FRF-CAP-T4M","FRF-FLOOR-TAM","FRF-FLOOR-T4M"))) {
		return C[0].C1.$size == 0 && C[0].C2.$size == 0 && C[0].C3.$size == 0;
    }    else {
		return C[0].C1.$size > 0;
    }
}else {
    return true;
}

if(F.$size > 0 && B.$size == 0 && C.$size == 0 && E.$size == 0) {
    $type = A[0].F77H[0].Type;
    $floatingRateOption = F[0].F14F[0].Floating_Rate_Option;
    if(($type == "ISDA" && In($floatingRateOption, "FRF-TAM-CDC","FRF-T4M-CDC","FRF-T4M-CDCCOMP","FRF-TAG-CDC","FRF-TAG-CDCCOMP","FRF-TMP-CDCAVERAG")) || 
         ($type == "AFB" && In($floatingRateOption, "FRF-SWAP-AMR","FRF-SWAP-TMP-IF","FRF-SWAP-TMP-M","FRF-SWAP-T4M-AMR","FRF-CAP-TAM","FRF-CAP-T4M","FRF-FLOOR-TAM","FRF-FLOOR-T4M"))) {
		return F[0].F1.$size == 0 && F[0].F2.$size == 0 && F[0].F3.$size == 0;
    }    else {
		return F[0].F1.$size > 0;
    }
}else {
    return true;
}

if(B.$size > 0 && F.$size > 0 && C.$size == 0 && E.$size == 0) {
    $type = A[0].F77H[0].Type;
    $floatingRateOption = F[0].F14F[0].Floating_Rate_Option;
    if(($type == "ISDA" && In($floatingRateOption, "FRF-TAM-CDC","FRF-T4M-CDC","FRF-T4M-CDCCOMP","FRF-TAG-CDC","FRF-TAG-CDCCOMP","FRF-TMP-CDCAVERAG")) || 
         ($type == "AFB" && In($floatingRateOption, "FRF-SWAP-AMR","FRF-SWAP-TMP-IF","FRF-SWAP-TMP-M","FRF-SWAP-T4M-AMR","FRF-CAP-TAM","FRF-CAP-T4M","FRF-FLOOR-TAM","FRF-FLOOR-T4M"))) {
		return B[0].B1.$size == 0 && F[0].F1.$size == 0 && F[0].F2.$size == 0 && F[0].F3.$size == 0 && B[0].F37U.$size > 0;
    }    else {
		return B[0].B1.$size > 0 && F[0].F1.$size > 0;
    }
}else {
    return true;
}

if(C.$size > 0 && E.$size > 0 && B.$size == 0 && F.$size == 0) {
    $type = A[0].F77H[0].Type;
    $floatingRateOption = C[0].F14F[0].Floating_Rate_Option;
    if(($type == "ISDA" && In($floatingRateOption, "FRF-TAM-CDC","FRF-T4M-CDC","FRF-T4M-CDCCOMP","FRF-TAG-CDC","FRF-TAG-CDCCOMP","FRF-TMP-CDCAVERAG")) || 
         ($type == "AFB" && In($floatingRateOption, "FRF-SWAP-AMR","FRF-SWAP-TMP-IF","FRF-SWAP-TMP-M","FRF-SWAP-T4M-AMR","FRF-CAP-TAM","FRF-CAP-T4M","FRF-FLOOR-TAM","FRF-FLOOR-T4M"))) {
		return E[0].E1.$size == 0 && C[0].C1.$size == 0 && C[0].C2.$size == 0 && C[0].C3.$size == 0 && E[0].F37U.$size > 0;
    }    else {
		return E[0].E1.$size > 0 && C[0].C1.$size > 0;
    }
}else {
    return true;
}

if(B.$size > 0 && E.$size > 0 && C.$size == 0 && F.$size == 0) {
	return B[0].B1.$size > 0 && E[0].E1.$size > 0;
}else {
    return true;
}

if(C.$size > 0 && F.$size > 0 && B.$size == 0 && E.$size == 0) {
    $type = A[0].F77H[0].Type;
    $floatingRateOption = C[0].F14F[0].Floating_Rate_Option;
    if(($type == "ISDA" && In($floatingRateOption, "FRF-TAM-CDC","FRF-T4M-CDC","FRF-T4M-CDCCOMP","FRF-TAG-CDC","FRF-TAG-CDCCOMP","FRF-TMP-CDCAVERAG")) || 
         ($type == "AFB" && In($floatingRateOption, "FRF-SWAP-AMR","FRF-SWAP-TMP-IF","FRF-SWAP-TMP-M","FRF-SWAP-T4M-AMR","FRF-CAP-TAM","FRF-CAP-T4M","FRF-FLOOR-TAM","FRF-FLOOR-T4M"))) {
		return C[0].C1.$size == 0 && C[0].C2.$size == 0 && C[0].C3.$size == 0;
    }    else {
		return C[0].C1.$size > 0;
    }
}else {
    return true;
}

if(C.$size > 0 && F.$size > 0 && B.$size == 0 && E.$size == 0) {
    $type = A[0].F77H[0].Type;
    $floatingRateOption = F[0].F14F[0].Floating_Rate_Option;
    if(($type == "ISDA" && In($floatingRateOption, "FRF-TAM-CDC","FRF-T4M-CDC","FRF-T4M-CDCCOMP","FRF-TAG-CDC","FRF-TAG-CDCCOMP","FRF-TMP-CDCAVERAG")) || 
         ($type == "AFB" && In($floatingRateOption, "FRF-SWAP-AMR","FRF-SWAP-TMP-IF","FRF-SWAP-TMP-M","FRF-SWAP-T4M-AMR","FRF-CAP-TAM","FRF-CAP-T4M","FRF-FLOOR-TAM","FRF-FLOOR-T4M"))) {
		return F[0].F1.$size == 0 && F[0].F2.$size == 0 && F[0].F3.$size == 0;
    }    else {
		return F[0].F1.$size > 0;
    }
}else {
    return true;
}

if(A[0].F94A.$size == 0 || 
     (A[0].F94A.$size > 0 && 
        In(A[0].F94A[0].Scope, "AGNT", "BILA"))) {
	return If(N.$size > 0, N[0].F71F.$size == 0, true);
}else {
	return N.$size > 0 && N[0].F88a.$size > 0;
}

if(C.$size > 0 && F.$size > 0 && In(A[0].F23A[0].Type_of_Swap, "COLLARBYER", "COLLARSLLR")) {
   return (C[0].F37J.$size > 0 || C[0].F37L.$size > 0) &&
          (F[0].F37J.$size > 0 || F[0].F37L.$size > 0) &&
          If(C[0].F37J.$size == 0, F[0].F37J.$size > 0, true) &&
          If(C[0].F37L.$size == 0, F[0].F37L.$size > 0, true);
}
else {
    return true;
}

def header = $this.Header;
def appheader;
def commonRef = $value.Bank_Code_1 + $value.Location_Code_1 + $value.Reference_Code + 
                  $value.Bank_Code_2 + $value.Location_Code_2;
if(Length(commonRef) != 16) {
    return false;
}
if(header.GetFieldCount() == 0 || 
     (header.GetFieldCount() > 0 && header.GetFieldIndex("Basic_Header") == -1)) {
    // No Header or FICC Header
    return true;
}
else if(header.GetFieldCount() == 4) {
    // Input-Output Header
    appheader = If(header.GetSection(1).$size > 0, 
                   header.GetSection(1)[0], 
                   header.GetSection(2)[0]);
}
else {
    appheader = header.GetSection(1)[0];
}
def basicheader = header.GetSection(0)[0];
def identifier = appheader.GetString("Input/Output Identifier");
def recvAddr;
def senderAddr;
if(identifier == "I") {
    senderAddr = basicheader.GetString("LT Identifier");
    recvAddr = appheader.GetString("Receiver's Address");
}
else if(identifier == "O") {
    recvAddr = basicheader.GetString("LT Identifier");
    senderAddr = appheader.GetString("Message Input Reference").Mid(6, 12);
}
// Remove Country Code from BIC
def senderBankLocCode = senderAddr.Mid(0, 8).Delete(4, 2);
def receiverBankLocCode = recvAddr.Mid(0, 8).Delete(4, 2);
def firstcode = Left(commonRef, 6);
def secondcode = Right(commonRef, 6);
def bankLocCodes = [senderBankLocCode, receiverBankLocCode];
def Field22Codes = [firstcode, secondcode];
return ContainsAll(Field22Codes, bankLocCodes);
