fieldvalidation.formula:
if($this.Header.GetFieldIndex("User_Header") != -1) {
    def userHeader = $this.Header.GetSection("User_Header");
    if(userHeader.$size == 0) {
        return false;
    }
    else
        if(userHeader[0].IsNull("Validation_Flag")) {
            return false;
        }
        else
            if(userHeader[0].GetString("Validation_Flag") != "IRSLST") {
                return false;
            }
}
return true;
