fieldvalidation.formula:
$intVal = IsInt(total_sections) && 
            IsInt(section_number);
$totalSec = If($intVal, ToInt(total_sections), 0);
$seqNo = If($intVal, ToInt(section_number), 0);
If($intVal, 
   If($totalSec > 1 && ($seqNo == $totalSec), 
      Group_2.$size == 1, 
      true), 
   false);

$intVal = IsInt(total_sections) && 
            IsInt(section_number);
$totalSec = If($intVal, ToInt(total_sections), 0);
$seqNo = If($intVal, ToInt(section_number), 0);
If($intVal, 
   If($totalSec > 1 && ($seqNo != $totalSec), 
      Group_2.$size == 0, 
      true), 
   false);
