fieldvalidation.formula:
if($this.Header.GetFieldIndex("User_Header") != -1) {
    def userHeader = $this.Header.GetSection("User_Header");
    if(userHeader.$size > 0 && userHeader[0].IsNotNull("Validation_Flag")) {
        return false;
    }
}
return true;

def Actualvalue = replace($value, "\r\n", "\n");
def lines = Split("\n", Actualvalue);
if(lines.Length() > 1) {
    for(def i = 1; i < lines.Length(); ++i) {
        if(lines[i].FindFirst("/VALD/") != -1)
            return false;

    }
}
if(lines[0].FindFirst("/VALD/") != -1) {
    return VerifyFormat(lines[0], "/VALD/8!nCrLf") && 
             IsDate(Mid(lines[0], 6, 8), "yyyyMMdd");
}
        return true;

def Actualvalue = replace($value, "\r\n", "\n");
def lines = Split("\n", Actualvalue);
if(lines[0].StartsWith("/VALD/")) {
    if(lines.Length() < 2 || !lines[1].StartsWith("/SETC/"))
        return false;

    if(lines[1].StartsWith("/SETC/")) {
        if(!VerifyFormat(lines[1], "/SETC/3!aCrLf") || 
             !IsISOCurrencyCode(Mid(lines[1], 6, 3)))
            return false;

    }
}
if(lines.Length() > 1) {
    if(lines[1].StartsWith("/SETC/") && !lines[0].StartsWith("/VALD/"))
        return false;

    if(lines.Length() > 2 && lines[2].StartsWith("/SRCE/") && 
         !lines[1].StartsWith("/SETC/"))
        return false;

    }
for(def i = 0; i < lines.Length(); ++i) {
    if(i != 1 && lines[i].FindFirst("/SETC/") != -1)
        return false;

    if(i != 2 && lines[i].FindFirst("/SRCE/") != -1)
        return false;

    if(i == 2){
    	//Thirdline can have /SRCE/ only in the first 6 characters and nowhere
    	def str = RightStr(lines[i], "/SRCE/");
    	def index = FindFirst(RightStr(lines[i], "/SRCE/"), "/SRCE/");
    	if(index != -1 || str.StartsWith("SRCE/"))
	        return false;
        }

}
return true;

def header = $this.Header;
def appheader;
def commonRef = $value.Bank_Code_1 + $value.Location_Code_1 + $value.Reference_Code + 
                  $value.Bank_Code_2 + $value.Location_Code_2;
if(Length(commonRef) != 16){
  return false;
}
if(header.GetFieldCount() == 0 || 
     (header.GetFieldCount() > 0 && header.GetFieldIndex("Basic_Header") == -1)) {
	// No Header or FICC Header
	return true;
}
else if(header.GetFieldCount() == 4) {
	// Input-Output Header
    appheader = If(header.GetSection(1).$size > 0, 
                   header.GetSection(1)[0], 
                   header.GetSection(2)[0]);
}
else  {
    appheader = header.GetSection(1)[0];
}
def basicheader = header.GetSection(0)[0];
def identifier = appheader.GetString("Input/Output Identifier");
def recvAddr;
def senderAddr;
if(identifier == "I") {
	senderAddr = basicheader.GetString("LT Identifier");
    recvAddr = appheader.GetString("Receiver's Address");
}
else if(identifier == "O") {
    recvAddr = basicheader.GetString("LT Identifier");
   	senderAddr = appheader.GetString("Message Input Reference").Mid(6, 12);
}
// Remove Country Code from BIC
def senderBankLocCode = senderAddr.Mid(0, 8).Delete(4, 2);
def receiverBankLocCode = recvAddr.Mid(0, 8).Delete(4, 2);
def firstcode = Left(commonRef, 6);
def secondcode = Right(commonRef, 6);
def bankLocCodes = [senderBankLocCode, receiverBankLocCode];
def Field22Codes = [firstcode, secondcode];
return ContainsAll(Field22Codes, bankLocCodes) && ContainsAll(bankLocCodes, Field22Codes);

FormulaFunctionDef.Code:
def fldcount = 0;
for(def i = 0; i < Obj[0].GetFieldCount(); ++i) {
	def count = Obj[0].GetFieldCount();
    def fld = Obj[0].GetSection(i);
    if(fld.$size > 0)
	    fldcount += 1;
    if(fldcount > 0)
	    break;
}
if(fldcount == 0)
    return false;
return true;
