fieldvalidation.formula:
if($this.Header.GetFieldIndex("User_Header") != -1) {
    def userHeader = $this.Header.GetSection("User_Header");
    if(userHeader.$size > 0 && userHeader[0].IsNotNull("Validation_Flag")) {
        if(userHeader[0].GetString("Validation_Flag") != "ISLFIN") {
            return false;
        }
    }
}
return true;

if(IsNotNull($value.Description_Of_Security) && IsNull($value.Identification_Of_Security)) {
    def Actual_Description_Value = replace($value.Description_Of_Security, "\r\n", "\n");
def lines = Split("\n", Actual_Description_Value);
    if(EqualIgnoreCase(Left(lines[0], 4), "ISIN"))
                return (Equal(Left(lines[0], 5), "ISIN "));
}
return true;

def DSSabsent = $value.F25D.GetStrings("*.Data_Source_Scheme").Length() == 0;
def statusCodes = $value.F25D.GetStrings("*.Status_Code");
if(DSSabsent) {
    foreach(def A2a in $value.A2a) {
        def F24B = A2a.F24B[0];
        for(def i = 0; i < F24B.GetFieldCount(); ++i) {
            def qualSec = F24B.GetSection(i);
            if(qualSec.$size > 0) {
                def qualifier = F24B.GetFieldName(i);
                if(statusCodes.Length() == 0 || qualifier != statusCodes[0])
                    return false;

            }
        }
    }
}
return true;
