def fldcount = 0;
for(def i = 0; i < Obj[0].GetFieldCount(); ++i) {
	def count = Obj[0].GetFieldCount();
    def fld = Obj[0].GetSection(i);
    if(fld.$size > 0)
	    fldcount += 1;
    if(fldcount > 0)
	    break;
}
if(fldcount == 0)
    return false;
return true;
